
function ChineseLength(a,b,c,d){this.id=a,this.max_size=b,this.success_callback=c,this.error_callback=d}ChineseLength.prototype.listen=function(){var a=this;$("#"+this.id).bind("keyup change",function(){var b=a.get_length();b<=a.max_size?a.success_callback():a.error_callback()})},ChineseLength.prototype.get_length=function(){var a,b,c=$.trim($("#"+this.id).val()),d=0;for(a=0,b=c.length;b>a;a+=1)d+=null!=c[a].match(/[^\x00-\xff]/gi)?2:1;return d};
